(()=>{"use strict";document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".fade-in-section");if(0===e.length)return;const t=new IntersectionObserver((e,t)=>{e.forEach(e=>{e.isIntersecting&&(e.target.classList.add("is-visible"),t.unobserve(e.target))})},{threshold:.1});e.forEach(e=>{t.observe(e)})})})();